package com.tns.dayone;

public class First {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=20;  

		int b=6;  

		

		//Arithmetic Operator

		System.out.println("Arithmetic Operator");

		System.out.println(a+b);  

		System.out.println(a-b); 

		System.out.println(a*b); 

		System.out.println(a/b);

		System.out.println(a%b);

	}

}
